package com.run.leetcode;

public class BitProperties {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(1<<5);
System.out.println(Integer.toBinaryString(1));
System.out.println(Integer.toBinaryString(5));
System.out.println(Integer.toBinaryString(1<<5));//Left shift is multiplying with 2 to the power of right value to left valye.Here 1 * 2^5
System.out.println(Integer.toBinaryString(-1<<5));

System.out.println(Integer.toBinaryString(100));
System.out.println(Integer.toBinaryString(20));
System.out.println(Integer.toBinaryString(100>>20));

;
	}

}
